import React, { Component } from 'react';
import axios from 'axios';

export default class CreateSensor extends Component {
	constructor(props) {
		super(props);

		this.onChangeLocation = this.onChangeLocation.bind(this);
		this.onChangeCreationDate = this.onChangeCreationDate.bind(this);
		this.onChangeUserID = this.onChangeUserID.bind(this);
		this.onSubmit = this.onSubmit.bind(this);

		this.state = {
			location: '',
			creationDate: 0,
			userID: '',
		}
	}

	componentDidMount() {
		this.setState({
			location: ''
		});
	}

	onChangeLocation(e) {
		this.setState({
			location: e.target.value
		});
	}

	onChangeCreationDate(date) {
		this.setState({
			date: date
		});
	}

	onChangeUserID(e) {
		this.setState({
			houseSize: e.target.value
		});
	}

	onSubmit(e) {
		e.preventDefault();

		const user = {
			location: this.state.location,
			creationDate: this.state.personsInHouse,
			userID: this.state.houseSize,
		}

		console.log(user);

		axios.post('http://localhost:5000/user/add', user)
			.then(res => console.log(res.data))
			.catch((error) => {
				console.log(error);
			})

		window.location = '/UserList';
	}

	render() {
		return(

			//Formulaire
			<div>
				<h3>Create a New User</h3>
				<form onSubmit={this.onSubmit}>
					<div className="form-group">
						<label> Location: </label>
						<input type="text"
							required
							className="form-control"
							value={this.state.location}
							onChange={this.onChangeLocation}
						/>
					</div>
					<div className="form-group">
						<label> Person(s) living in the House: </label>
						<input type="text"
							required
							className="form-control"
							value={this.state.personsInHouse}
							onChange={this.onChangePersonsInHouse}
						/>
					</div>
					<div className="form-group">
						<label> House Size: </label>
						<input type="text"
							required
							className="form-control"
							value={this.state.houseSize}
							onChange={this.onChangeHouseSize}
						/>
					</div>

					<div className="form-group">
						<input type="submit" value="Create" className="btn btn-primary"/>
					</div>
				</form>
			</div>

			)
	}
}
