import React, { Component } from 'react';

export default class CreateUser extends Component {
	constructor(props) {
		super(props);

		this.state = {
			location: '',
			personsInHouse: 0,
			houseSize: '',
			users: []
		}
	}

	onChangeLocation(e) {
		this.setState({
			location: e.target.value
		});
	}

	onChangePersonsInHouse(e) {
		this.setState({
			personsInHouse: e.target.value
		});
	}

	onChangeHouseSize(e) {
		this.setState({
			houseSize: e.target.value
		});
	}


}
