import React, { Component } from 'react';
import axios from 'axios';

export default class CreateMeasure extends Component {
	constructor(props) {
		super(props);

		this.onChangeType = this.onChangeType.bind(this);
		this.onChangeDate = this.onChangeDate.bind(this);
		this.onChangeSensorID = this.onChangeSensorID.bind(this);
		this.onChangeValue = this.onChangeValue.bind(this);
		this.onSubmit = this.onSubmit.bind(this);

		this.state = {
			type: '',
			creationDate: '',
			sensorID: '',
			sensors: [],
		}
	}

	componentDidMount() {
		axios.get('http://localhost:5000/sensor/')
			.then(response => {
				if(response.data.length > 0) {
					this.setState({
						sensors: response.data.map(sensor=> user._id),
						sensorID: response.data[0]._id
					})
				}
			})
	}

	onChangeType(e) {
		this.setState({
			type: e.target.value
		});
	}

	onChangeDate(e) {
		this.setState({
			creationDate: e.target.value
		});
	}

	onChangeSensorID(e) {
		this.setState({
			sensorID: e.target.value
		});
	}

	onSubmit(e) {
		e.preventDefault();

		const measure = {
			type: this.state.type,
			creationDate: this.state.creationDate,
			sensorID: this.state.sensorID,
		}

		console.log(measure);

		axios.post('http://localhost:5000/measure/add', measure)
			.then(res => console.log(res.data))
			.catch((error) => {
				console.log(error);
			})

		window.type = '/MeasureList';
	}

	render() {
		return(

			//Formulaire
			<div>
				<h3>Create a New Measure</h3>
				<form onSubmit={this.onSubmit}>

					<div className="form-group">
						<label> User ID: </label>
						<select ref="userInput"
						required
						className="form-control"
						value={this.onChangeSensorID}>
						{
							this.state.sensors.map(function(user) {
								return <option
								key={user}
								value={user}>
								{user}
								</option>;
							})
						}
						</select>

					</div>

					<div className="form-group">
						<label> Type (Country): </label>
						<input type="text"
							required
							className="form-control"
							value={this.state.type}
							onChange={this.onChangeType}
						/>
					</div>

					<div className="form-group">
						<label> Creation Date (MM/DD/YYYY): </label>
						<input type="text"
							required
							className="form-control"
							value={this.state.creationDate}
							onChange={this.onChangeDate}
						/>
					</div>

					<div className="form-group">
						<input type="submit" value="Create" className="btn btn-primary"/>
					</div>
				</form>
			</div>

			)
	}
}
