import React, { Component } from 'react';
import axios from 'axios';

export default class CreateSensor extends Component {
	constructor(props) {
		super(props);

		this.onChangeLocation = this.onChangeLocation.bind(this);
		this.onChangeCreationDate = this.onChangeCreationDate.bind(this);
		this.onChangeUserID = this.onChangeUserID.bind(this);
		this.onSubmit = this.onSubmit.bind(this);

		this.state = {
			location: '',
			creationDate: 0,
			userID: '',
			users: [],
		}
	}

	componentDidMount() {
		this.setState({
			users: ['test user']
		});
	}

	onChangeLocation(e) {
		this.setState({
			location: e.target.value
		});
	}

	onChangeCreationDate(date) {
		this.setState({
			date: date
		});
	}

	onChangeUserID(e) {
		this.setState({
			userID: e.target.value
		});
	}

	onSubmit(e) {
		e.preventDefault();

		const sensor = {
			location: this.state.location,
			creationDate: this.state.creationDate,
			userID: this.state.userID,
		}

		console.log(sensor);

		axios.post('http://localhost:5000/sensor/add', sensor)
			.then(res => console.log(res.data))
			.catch((error) => {
				console.log(error);
			})

		window.location = '/SensorList';
	}

	render() {
		return(

			//Formulaire
			<div>
				<h3>Create a New Sensor</h3>
				<form onSubmit={this.onSubmit}>
					<div className="form-group">
						<label> Location: </label>
						<input type="text"
							required
							className="form-control"
							value={this.state.location}
							onChange={this.onChangeLocation}
						/>
					</div>
					<div className="form-group">
						<label> Person(s) living in the House: </label>
						<input type="text"
							required
							className="form-control"
							value={this.state.personsInHouse}
							onChange={this.onChangePersonsInHouse}
						/>
					</div>
					<div className="form-group">
						<label> User ID: </label>
						<select ref="userInput"
						required
						className="form-control"
						value={this.onChangeUserID}>
						{
							this.state.users.map(function(user) {
								return <option
								key={user}
								value={user}>
								{user}
								</option>;
							})
						}
						</select>

					</div>

					<div className="form-group">
						<input type="submit" value="Create" className="btn btn-primary"/>
					</div>
				</form>
			</div>

			)
	}
}
